mapper.insert:

	select calendar_no_seq.nextval from dual

insert into calendar(
	cal_no, title, start_date, end_date
)values(
	#{calNo}, #{title}, #{startDate}, #{endDate}
)

mapper.delete:
delete from calendar where cal_no in 
(select cal_no
	from calendar
	where title = #{title}
	and start_date like #{startDate} || '%'
)
